able_idx (slug), INDEX promo1_gallery_id_idx (promo1_gallery_id), INDEX promo2_gallery_id_idx (promo2_gallery_id), INDEX promo3_gallery_id_idx (promo3_gallery_id), INDEX promo4_gallery_id_idx (promo4_gallery_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_czech_ci ENGINE = INNODB;
